import { Badge, Button, Card, Col, Image, Pagination, Result, Row, Skeleton, Space, Typography } from 'antd';
import { history, IRouteComponentProps } from 'umi';
import { useContext, useEffect, useState } from 'react';
import axios from 'axios';
import { UserInfo, UserLogged } from '@/layouts/App';
import { ShoppingCartOutlined } from '@ant-design/icons';
import Layout from '@/layouts/Layout';
import Price from '@/Components/Price';


export default function Index(props: IRouteComponentProps) {
  const [data, setData] = useState<IVehicleRent[]>([]);
  // @ts-ignore
  const [page, setPage] = useState(parseInt(props.location.query.page ?? 1));
  const [loading, setLoading] = useState(true);
  const [total, setTotal] = useState(0);
  const { authenticated } = useContext(UserLogged);
  const { discount } = useContext(UserInfo);

  useEffect(() => {
    if (authenticated) {
      axios.get('/vehicles-rent/', {
          params: {
            page: page,
          },
        },
      ).then((response) => {
        const data: PageResponse<IVehicleRent> = response.data;

        setData(data.results);
        setTotal(data.count);
        setLoading(false);
      });
    }
  }, [page]);

  const vehicleCards = data.map((item: IVehicleRent) => {

    const description = <Typography.Text>Model: {item.vehicle.model}, seats: {item.vehicle.seats}, vehicle
      type: {item.vehicle.type}, fuel type: {item.vehicle.fuel_type}, price: <Price price={item.price}
                                                                                    discount={discount}
                                                                                    discountValue={15} /></Typography.Text>;

    return (
      <Col lg={8} key={item.id}>
        <Badge.Ribbon text={item.count === 0 ? 'Out of stock' : `Available in stock: ${item.count}`}
                      color={item.count === 0 ? 'red' : ''}>
          <Card
            hoverable
            cover={
              <Image
                alt={item.vehicle.model}
                src={item.picture}
                height={300}
                fallback='data:image/png;base64,'
              />
            }
            actions={[
              <Typography.Link onClick={() => history.push(`/vehicle/${item.id}/rent`)}><ShoppingCartOutlined
                key='shop' /></Typography.Link>,
            ]}
          >
            <Card.Meta title={item.vehicle.brand} description={description} />
          </Card>
        </Badge.Ribbon>
      </Col>
    );
  });

  return (
    <Layout>
      {authenticated ? (
        <>
          <Skeleton active loading={loading} />
          <Space align={'center'} size={'large'} direction={'vertical'}>
            <Row gutter={[24, 24]} justify={'space-around'}>
              {vehicleCards}
            </Row>
            <Pagination
              defaultCurrent={page}
              total={total}
              hideOnSinglePage
              onChange={(page, pageSize) => {
                setPage(page);
              }}
              showSizeChanger={false}
              defaultPageSize={25}
            />
          </Space>
        </>
      ) : (
        <Result
          status='warning'
          title='Sorry, you are not authorized to access this page.'
          extra={
            <Button type='primary' onClick={() => history.push('/login')}>
              Log in
            </Button>
          }
        />
      )}
    </Layout>
  );
}
